accepting_states:
    db 0, 1, 1, 0, 1, 1, 1, 0, 0

input_str: db 'a', 'c', 'b', 'f', 0

accepted_str: db "Accepted", 10, 0
rejected_str: db "Rejected", 10, 0

section .text
global _start

_start:
    mov esi, input_str
    mov ebx, 0

loop:
    mov al, [esi]
    cmp al, 0
    je check_accept

    movzx eax, al
    mov ecx, ebx
    shl ecx, 8
    add ecx, eax
    mov bl, [trans_table + ecx]
    movzx ebx, bl
    inc esi
    jmp loop

check_accept:
    cmp byte [accepting_states + ebx], 1
    je accept
    jmp reject

accept:
    mov eax, 4
    mov ebx, 1
    mov ecx, accepted_str
    mov edx, 10
    int 0x80
    jmp end

reject:
    mov eax, 4
    mov ebx, 1
    mov ecx, rejected_str
    mov edx, 10
    int 0x80
    jmp end

end:
    mov eax, 1
    mov ebx, 0
    int 0x80
